import React, { useState, useEffect } from "react";
import moment from "moment";
import "./App.css";
import jsonData from "../src/jsonData/chatHistory.json";

function App() {
  // State variables
  const [conversations, setConversations] = useState(sampleData);
  const [selectedConversationId, setSelectedConversationId] = useState(null);
  const [newMessage, setNewMessage] = useState("");
  const [editMode, setEditMode] = useState(false);
  const [editMessageId, setEditMessageId] = useState(null);
  const [error, setError] = useState("");

  // Find the selected conversation based on its ID
  const selectedConversation = conversations.find(
    (conv) => conv.id === selectedConversationId
  );

  // Function to handle sending or editing a message
  const handleSendMessage = () => {
    try {
      // Validate that a conversation is selected
      if (!selectedConversationId) {
        throw new Error("Please select a conversation.");
      }

      // Validate that the message is not empty
      if (newMessage.trim() === "") {
        throw new Error("Message cannot be empty.");
      }

      // Update conversations using the previous state
      setConversations((prevConversations) => {
        // Map over each conversation
        const updatedConversations = prevConversations.map((conv) => {
          // Check if the current conversation is the selected one
          if (conv.id === selectedConversationId) {
            // Update the messages for the selected conversation
            const updatedMessages = editMode
              ? conv.messages.map((msg) =>
                  msg.id === editMessageId
                    ? {
                        ...msg,
                        text: newMessage,
                        last_updated: moment().toISOString(),
                      }
                    : msg
                )
              : [
                  ...conv.messages,
                  {
                    id: conv.messages.length + 1,
                    text: newMessage,
                    last_updated: moment().toISOString(),
                  },
                ];

            // Return the updated conversation with the new messages
            return {
              ...conv,
              messages: updatedMessages,
              last_updated: moment().toISOString(),
            };
          }
          // If it's not the selected conversation, return the unchanged conversation
          return conv;
        });

        // Return the updated conversations to setConversations
        return updatedConversations;
      });

      // Reset input fields and errors after a successful update
      setNewMessage("");
      setEditMode(false);
      setEditMessageId(null);
      setError("");
    } catch (error) {
      // Handle errors by updating the error state
      setError(error.message);
    }
  };

  // Use useEffect to sort conversations whenever conversations or selectedConversationId change
  useEffect(() => {
    // Sort conversations by last_updated timestamp in descending order
    const sortedConversations = [...conversations].sort(
      (a, b) => new Date(b.last_updated) - new Date(a.last_updated)
    );

    // Update the state with the sorted conversations
    setConversations(sortedConversations);
  }, [conversations, selectedConversationId]);

  // ... (unchanged)

  // Render the application
  return (
    <div className="App">
      {/* Left side: Conversation list */}
      <div className="conversation-list">
        <h2>Conversations</h2>
        <ul>
          {/* Map over each conversation to display in the list */}
          {conversations.map((conv) => (
            <li
              key={conv.id}
              onClick={() => setSelectedConversationId(conv.id)}
            >
              {conv.name}
            </li>
          ))}
        </ul>
      </div>

      {/* Right side: Messages */}
      <div className="messages">
        <h2>Messages</h2>
        {selectedConversation ? (
          <>
            {/* Display messages and reply section */}
            <ul>
              {/* Map over each message in the selected conversation to display in the list */}
              {selectedConversation.messages.map((msg) => (
                <li key={msg.id} onClick={() => handleEditMessage(msg)}>
                  <p>{moment(msg.last_updated).format("LLL")}</p>
                  <p>{msg.text}</p>
                </li>
              ))}
            </ul>
            {/* Reply section with input field, button, and error display */}
            <div className="reply-section">
              <input
                type="text"
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
              />
              <button onClick={handleSendMessage}>
                {editMode ? "Edit" : "Send"}
              </button>
              {error && <p style={{ color: "red" }}>{error}</p>}
            </div>
          </>
        ) : (
          // Display message if no conversation is selected
          <p>Select a conversation to view messages.</p>
        )}
      </div>
    </div>
  );
}

export default App;
