import React, { useState, useEffect } from "react";
import moment from "moment";
import "./App.css";
import jsonData from "../src/jsonData/chatHistory.json";

function App() {
  // State variables for managing application state
  const [conversations, setConversations] = useState(jsonData);
  const [selectedConversation, setSelectedConversation] = useState(null);
  const [newMessage, setNewMessage] = useState("");
  const [editMode, setEditMode] = useState(false);
  const [editMessageId, setEditMessageId] = useState(null);
  const [error, setError] = useState("");

  // Function to handle clicking on a conversation
  const handleConversationClick = (conversation) => {
    // Set the selected conversation and reset state for new messages or edits
    setSelectedConversation(conversation);
    setEditMode(false);
    setEditMessageId(null);
    setNewMessage("");
  };

  // Function to handle sending or editing a message
  const handleSendMessage = () => {
    try {
      if (!selectedConversation) {
        throw new Error("Please select a conversation.");
      }

      if (newMessage.trim() === "") {
        throw new Error("Message cannot be empty.");
      }

      const updatedConversations = conversations.map((conv) => {
        if (conv.id === selectedConversation.id) {
          return {
            ...conv,
            messages: editMode
              ? conv.messages.map((msg) =>
                  msg.id === editMessageId
                    ? {
                        ...msg,
                        text: newMessage,
                        last_updated: moment().toISOString(),
                      }
                    : msg
                )
              : [
                  ...conv.messages,
                  {
                    id: conv.messages.length + 1,
                    text: newMessage,
                    last_updated: moment().toISOString(),
                  },
                ],
          };
        }
        return conv;
      });

      // Set the state with the updated conversations
      setConversations(updatedConversations);
      setNewMessage("");
      setEditMode(false);
      setEditMessageId(null);
      setError("");
    } catch (error) {
      setError(error.message);
    }
  };

  // Sort conversations when the component mounts or when conversations change
  useEffect(() => {
    const sortedConversations = [...conversations].sort(
      (a, b) => new Date(b.last_updated) - new Date(a.last_updated)
    );
    setConversations(sortedConversations);
  }, []);

  // Function to handle clicking on a message for editing
  const handleEditMessage = (message) => {
    // Set the message text for editing and enable edit mode
    setNewMessage(message.text);
    setEditMode(true);
    setEditMessageId(message.id);
  };

  // Sort conversations by last_updated timestamp in descending order
  const sortedConversations = conversations.sort(
    (a, b) => new Date(b.last_updated) - new Date(a.last_updated)
  );

  // Sort messages by last_updated timestamp in ascending order
  const sortedMessages =
    selectedConversation &&
    selectedConversation.messages.sort(
      (a, b) => new Date(a.last_updated) - new Date(b.last_updated)
    );

  // Render the application
  return (
    <div className="App">
      {/* Left side: Conversation list */}
      <div className="conversation-list">
        <h2>Conversations</h2>
        <ul>
          {sortedConversations.map((conv) => (
            // Display each conversation in the list
            <li key={conv.id} onClick={() => handleConversationClick(conv)}>
              {conv.name}
            </li>
          ))}
        </ul>
      </div>

      {/* Right side: Messages */}
      <div className="messages">
        <h2>Messages</h2>
        {selectedConversation ? (
          <>
            {/* Display messages and reply section */}
            <ul>
              {sortedMessages.map((msg) => (
                // Display each message in the list, enable editing on click
                <li key={msg.id} onClick={() => handleEditMessage(msg)}>
                  <p>{moment(msg.last_updated).format("LLL")}</p>
                  <p>{msg.text}</p>
                </li>
              ))}
            </ul>
            <div className="reply-section">
              {/* Input field for new message */}
              <input
                type="text"
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
              />
              {/* Send or Edit button */}
              <button onClick={handleSendMessage}>
                {editMode ? "Edit" : "Send"}
              </button>
              {/* Display error message if any */}
              {error && <p style={{ color: "red" }}>{error}</p>}
            </div>
          </>
        ) : (
          // Display message if no conversation is selected
          <p>Select a conversation to view messages.</p>
        )}
      </div>
    </div>
  );
}

export default App;
